import React, { useState } from 'react';
import './SlideBar.css';
import AdminPanel from './AdminPanel';
import LogsNavigation from './LogsNavigation';

const SlideBar = ({ isOpen, onClose, activeView, user }) => {
  const [slideActiveView, setSlideActiveView] = useState('admin');

  const handleViewChange = (view) => {
    setSlideActiveView(view);
  };

  const renderSlideContent = () => {
    switch (slideActiveView) {
      case 'admin':
        return <AdminPanel />;
      case 'logs':
        return <LogsNavigation />;
      default:
        return <AdminPanel />;
    }
  };

  return (
    <>
      {/* Overlay */}
      {isOpen && (
        <div className="slide-overlay" onClick={onClose}></div>
      )}

      {/* Slide Bar */}
      <div className={`slide-bar ${isOpen ? 'open' : ''}`}>
        {/* Header */}
        <div className="slide-header">
          <div className="slide-title">
            <h3>‚öôÔ∏è Tools Panel</h3>
            <p>Admin & Logs Management</p>
          </div>
          <button className="close-slide-btn" onClick={onClose}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
              <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" fill="currentColor"/>
            </svg>
          </button>
        </div>

        {/* Navigation Tabs */}
        <div className="slide-nav">
          <button
            className={`slide-nav-btn ${slideActiveView === 'admin' ? 'active' : ''}`}
            onClick={() => handleViewChange('admin')}
          >
            <span className="nav-icon">üëë</span>
            <span className="nav-label">Admin Panel</span>
          </button>
          <button
            className={`slide-nav-btn ${slideActiveView === 'logs' ? 'active' : ''}`}
            onClick={() => handleViewChange('logs')}
          >
            <span className="nav-icon">üìù</span>
            <span className="nav-label">Logs</span>
          </button>
        </div>

        {/* Content Area */}
        <div className="slide-content">
          {renderSlideContent()}
        </div>
      </div>
    </>
  );
};

export default SlideBar;
