import React, { useState, useEffect } from 'react';
import './Charts.css';
import {
  BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  AreaChart, Area, ScatterChart, Scatter, ZAxis, ComposedChart, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar
} from 'recharts';

const Charts = () => {
  const [chartData, setChartData] = useState({
    stageDistribution: [],
    areaTrends: [],
    timingAnalysis: [],
    utilizationStats: [],
    congestionMetrics: [],
    runtimeTrends: [],
    statusBreakdown: [],
    projectPerformance: []
  });
  const [loading, setLoading] = useState(true);
  const [activeChart, setActiveChart] = useState('stages');
  const [dateFilter, setDateFilter] = useState({
    startDate: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0], // 30 days ago
    endDate: new Date().toISOString().split('T')[0] // today
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchChartData();
  }, [dateFilter]);

  const fetchChartData = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5000/api/data/engineer-charts?startDate=${dateFilter.startDate}&endDate=${dateFilter.endDate}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setChartData(data);
      }
    } catch (error) {
      console.error('Error fetching chart data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDateFilterChange = (field, value) => {
    setDateFilter(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const resetDateFilter = () => {
    setDateFilter({
      startDate: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
      endDate: new Date().toISOString().split('T')[0]
    });
  };

  const COLORS = ['#28c76f', '#ea5455', '#ff9f43', '#7367f0', '#00d4aa', '#ff6b6b', '#4ecdc4', '#45b7d1'];

  // Chart Components
  const renderStageDistribution = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Stage-wise Distribution</h3>
        <div className="chart-legend">
          <span className="legend-item">
            <span className="legend-color" style={{ backgroundColor: '#28c76f' }}></span>
            Pass
          </span>
          <span className="legend-item">
            <span className="legend-color" style={{ backgroundColor: '#ea5455' }}></span>
            Fail
          </span>
        </div>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <BarChart data={chartData.stageDistribution} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis dataKey="stage" stroke="#b4b7bd" fontSize={12} />
            <YAxis stroke="#b4b7bd" fontSize={12} />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
            <Legend />
            <Bar dataKey="pass" stackId="a" fill="#28c76f" name="Pass" />
            <Bar dataKey="fail" stackId="a" fill="#ea5455" name="Fail" />
            <Bar dataKey="error" stackId="a" fill="#ff9f43" name="Error" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderAreaTrends = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Area Trends Over Time</h3>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <AreaChart data={chartData.areaTrends} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis dataKey="date" stroke="#b4b7bd" fontSize={12} />
            <YAxis stroke="#b4b7bd" fontSize={12} />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
            <Area 
              type="monotone" 
              dataKey="avgArea" 
              stroke="#7367f0" 
              fill="#7367f0" 
              fillOpacity={0.6}
              name="Average Area (μm²)"
            />
            <Area 
              type="monotone" 
              dataKey="maxArea" 
              stroke="#ff6b6b" 
              fill="#ff6b6b" 
              fillOpacity={0.4}
              name="Maximum Area (μm²)"
            />
          </AreaChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderTimingAnalysis = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Timing Analysis</h3>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <ComposedChart data={chartData.timingAnalysis} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis dataKey="project" stroke="#b4b7bd" fontSize={12} />
            <YAxis stroke="#b4b7bd" fontSize={12} />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
            <Legend />
            <Bar dataKey="internalTiming" fill="#28c76f" name="Internal Timing" />
            <Bar dataKey="interfaceTiming" fill="#ea5455" name="Interface Timing" />
            <Line type="monotone" dataKey="avgTiming" stroke="#7367f0" strokeWidth={2} name="Average Timing" />
          </ComposedChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderUtilizationStats = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Utilization Statistics</h3>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <ScatterChart margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis type="number" dataKey="area" name="Area (μm²)" stroke="#b4b7bd" />
            <YAxis type="number" dataKey="utilization" name="Utilization (%)" stroke="#b4b7bd" />
            <ZAxis range={[100, 400]} />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
            <Legend />
            <Scatter name="High Utilization" data={chartData.utilizationStats.filter(d => d.utilization > 80)} fill="#ea5455" />
            <Scatter name="Medium Utilization" data={chartData.utilizationStats.filter(d => d.utilization >= 60 && d.utilization <= 80)} fill="#ff9f43" />
            <Scatter name="Low Utilization" data={chartData.utilizationStats.filter(d => d.utilization < 60)} fill="#28c76f" />
          </ScatterChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderCongestionMetrics = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Congestion Metrics Heatmap</h3>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <BarChart data={chartData.congestionMetrics} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis dataKey="project" stroke="#b4b7bd" fontSize={12} />
            <YAxis stroke="#b4b7bd" fontSize={12} />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
            <Legend />
            <Bar dataKey="congestionScore" fill="#ff6b6b" name="Congestion Score" />
            <Bar dataKey="drcViolations" fill="#ea5455" name="DRC Violations" />
            <Bar dataKey="routingDensity" fill="#ff9f43" name="Routing Density" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderRuntimeTrends = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Runtime Trends</h3>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={chartData.runtimeTrends} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis dataKey="date" stroke="#b4b7bd" fontSize={12} />
            <YAxis stroke="#b4b7bd" fontSize={12} />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
            <Legend />
            <Line type="monotone" dataKey="avgRuntime" stroke="#7367f0" strokeWidth={2} name="Average Runtime (hrs)" />
            <Line type="monotone" dataKey="maxRuntime" stroke="#ea5455" strokeWidth={2} name="Maximum Runtime (hrs)" />
            <Line type="monotone" dataKey="minRuntime" stroke="#28c76f" strokeWidth={2} name="Minimum Runtime (hrs)" />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderStatusBreakdown = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Run Status Breakdown</h3>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <PieChart>
            <Pie
              data={chartData.statusBreakdown}
              cx="50%"
              cy="50%"
              outerRadius={120}
              dataKey="value"
              label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
              labelLine={false}
            >
              {chartData.statusBreakdown.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
          </PieChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderProjectPerformance = () => (
    <div className="chart-card">
      <div className="chart-header">
        <h3>Project Performance Radar</h3>
      </div>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <RadarChart data={chartData.projectPerformance}>
            <PolarGrid stroke="rgba(255,255,255,0.2)" />
            <PolarAngleAxis dataKey="metric" stroke="#b4b7bd" />
            <PolarRadiusAxis stroke="rgba(255,255,255,0.2)" />
            <Radar
              name="Performance Score"
              dataKey="value"
              stroke="#7367f0"
              fill="#7367f0"
              fillOpacity={0.3}
            />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'rgba(40, 48, 70, 0.95)',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '8px',
                color: '#d0d2d6'
              }}
            />
          </RadarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const chartTypes = [
    { id: 'stages', label: 'Stage Distribution', icon: '📊' },
    { id: 'area', label: 'Area Trends', icon: '📈' },
    { id: 'timing', label: 'Timing Analysis', icon: '⏱️' },
    { id: 'utilization', label: 'Utilization Stats', icon: '📊' },
    { id: 'congestion', label: 'Congestion Metrics', icon: '🔥' },
    { id: 'runtime', label: 'Runtime Trends', icon: '⏰' },
    { id: 'status', label: 'Status Breakdown', icon: '🥧' },
    { id: 'performance', label: 'Project Performance', icon: '🎯' }
  ];

  const renderActiveChart = () => {
    switch (activeChart) {
      case 'stages':
        return renderStageDistribution();
      case 'area':
        return renderAreaTrends();
      case 'timing':
        return renderTimingAnalysis();
      case 'utilization':
        return renderUtilizationStats();
      case 'congestion':
        return renderCongestionMetrics();
      case 'runtime':
        return renderRuntimeTrends();
      case 'status':
        return renderStatusBreakdown();
      case 'performance':
        return renderProjectPerformance();
      default:
        return renderStageDistribution();
    }
  };

  if (loading) {
    return (
      <div className="charts-loading">
        <div className="loading-spinner"></div>
        <p>Loading chart data...</p>
      </div>
    );
  }

  return (
    <div className="charts-container">
      {/* Header */}
      <div className="charts-header">
        <div className="header-content">
          <h1>📊 Engineer Analytics Dashboard</h1>
          <p>Comprehensive analysis of stages, trends, area, timing, utilization, and congestion metrics</p>
        </div>
        
        {/* Date Filter */}
        <div className="date-filter-section">
          <div className="filter-header">
            <h3>Date Range Filter</h3>
            <button className="reset-filter-btn" onClick={resetDateFilter}>
              Reset Filter
            </button>
          </div>
          <div className="filter-controls">
            <div className="date-input-group">
              <label>Start Date</label>
              <input
                type="date"
                value={dateFilter.startDate}
                onChange={(e) => handleDateFilterChange('startDate', e.target.value)}
                className="date-input"
              />
            </div>
            <div className="date-input-group">
              <label>End Date</label>
              <input
                type="date"
                value={dateFilter.endDate}
                onChange={(e) => handleDateFilterChange('endDate', e.target.value)}
                className="date-input"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Chart Type Selector */}
      <div className="chart-selector">
        {chartTypes.map((chart) => (
          <button
            key={chart.id}
            className={`chart-type-btn ${activeChart === chart.id ? 'active' : ''}`}
            onClick={() => setActiveChart(chart.id)}
          >
            <span className="chart-icon">{chart.icon}</span>
            <span className="chart-label">{chart.label}</span>
          </button>
        ))}
      </div>

      {/* Chart Display */}
      <div className="chart-display">
        {renderActiveChart()}
      </div>

      {/* Quick Stats */}
      <div className="quick-stats">
        <div className="stat-card">
          <h4>Total Runs</h4>
          <span className="stat-number">
            {chartData.stageDistribution.reduce((sum, item) => sum + item.pass + item.fail + item.error, 0)}
          </span>
        </div>
        <div className="stat-card">
          <h4>Success Rate</h4>
          <span className="stat-number">
            {(() => {
              const total = chartData.stageDistribution.reduce((sum, item) => sum + item.pass + item.fail + item.error, 0);
              const passed = chartData.stageDistribution.reduce((sum, item) => sum + item.pass, 0);
              return total > 0 ? `${((passed / total) * 100).toFixed(1)}%` : '0%';
            })()}
          </span>
        </div>
        <div className="stat-card">
          <h4>Avg Area</h4>
          <span className="stat-number">
            {chartData.areaTrends.length > 0 
              ? `${(chartData.areaTrends.reduce((sum, item) => sum + item.avgArea, 0) / chartData.areaTrends.length).toFixed(0)} μm²`
              : '0 μm²'
            }
          </span>
        </div>
        <div className="stat-card">
          <h4>Avg Utilization</h4>
          <span className="stat-number">
            {chartData.utilizationStats.length > 0 
              ? `${(chartData.utilizationStats.reduce((sum, item) => sum + item.utilization, 0) / chartData.utilizationStats.length).toFixed(1)}%`
              : '0%'
            }
          </span>
        </div>
      </div>
    </div>
  );
};

export default Charts;
