import React, { useState } from 'react';
import './Dashboard.css';
import AdminPanel from './AdminPanel';
import DataVisualization from './DataVisualization';
import LogsNavigation from './LogsNavigation';
import ManagerView from './ManagerView';
import LeadView from './LeadView';
import CustomerView from './CustomerView';
import ProjectFilter from './ProjectFilter';
import SlideBar from './SlideBar';
import UserProfilePage from './UserProfilePage';

function Dashboard({ user, onLogout, onUserUpdate }) {
  const [activeView, setActiveView] = useState(null); // No view by default
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [projectFilters, setProjectFilters] = useState({ domain_id: '', project_id: '' });
  const [clickedButton, setClickedButton] = useState(null);
  const [showProfilePage, setShowProfilePage] = useState(false);
  const [headerExpanded, setHeaderExpanded] = useState(false);

  // Check if user is a customer
  const isCustomer = user?.role_name === 'Customer';

  // Check if filters are active
  const filtersActive = projectFilters.project_id || projectFilters.domain_id;
  const viewActive = activeView && !showProfilePage;

  // Handle profile click from sidebar
  const handleProfileClick = () => {
    setShowProfilePage(true);
    setActiveView('profile'); // Set profile as active view
  };

  // Handle user data update from profile page
  const handleUserUpdate = (updatedUser) => {
    // Update user in localStorage
    localStorage.setItem('user', JSON.stringify(updatedUser));
    // Update user state in parent component
    if (typeof onUserUpdate === 'function') {
      onUserUpdate(updatedUser);
    }
  };

  // Handle view change with animation
  const handleViewChange = (viewId) => {
    setClickedButton(viewId);
    setActiveView(viewId);
    setShowProfilePage(false); // Hide profile page when switching views

    // Reset the clicked state after animation
    setTimeout(() => {
      setClickedButton(null);
    }, 300);
  };

  // Handle header click to expand/collapse
  const handleHeaderClick = () => {
    if (filtersActive && viewActive) {
      setHeaderExpanded(!headerExpanded);
    }
  };

  // Only show Engineer, Lead, Manager as view tabs (not for customers)
  const getTabViews = () => {
    // If user is a customer, don't show any tab views
    if (isCustomer) {
      return [];
    }

    const views = [];
    if (projectFilters.domain_id) {
      views.push(
      { id: 'engineer', label: 'Engineer View', icon: '‚öôÔ∏è' },
      { id: 'lead', label: 'Lead View', icon: 'üë•' },
        { id: 'manager', label: 'Manager View', icon: 'üìà' }
      );
    }
    return views;
  };

  // Admin Panel and Logs only from sidebar (not for customers)
  const renderDashboardContent = () => {
    // If profile page is active, show it
    if (showProfilePage) {
      return <UserProfilePage user={user} onLogout={onLogout} onUserUpdate={handleUserUpdate} />;
    }

    // If user is a customer, always show CustomerView
    if (isCustomer) {
      return <CustomerView user={user} projectFilters={projectFilters} />;
    }

    if (activeView === 'admin') return <AdminPanel />;
    if (activeView === 'logs') return <LogsNavigation />;
    if (!activeView) {
      return (
        <div className="no-view-selected">
          <div className="no-view-content">
            <div className="welcome-message">
              <h2>Welcome to the Dashboard</h2>
              <p>Please select a view from the sidebar or choose Engineer, Lead, or Manager to see dashboard data.</p>
              <div className="welcome-actions">
                <div className="welcome-tips">
                  <div className="tip-item">
                    <span className="tip-icon">‚öôÔ∏è</span>
                    <span><strong>Engineer View:</strong> Detailed technical metrics and data visualization</span>
                  </div>
                  <div className="tip-item">
                    <span className="tip-icon">üë•</span>
                    <span><strong>Lead View:</strong> Team management and project oversight</span>
                  </div>
                  <div className="tip-item">
                    <span className="tip-icon">üìà</span>
                    <span><strong>Manager View:</strong> High-level analytics and reporting</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }
    switch (activeView) {
      case 'engineer':
        return <DataVisualization projectFilters={projectFilters} />;
      case 'lead':
        return <LeadView user={user} projectFilters={projectFilters} />;
      case 'manager':
        return <ManagerView user={user} projectFilters={projectFilters} />;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar - Only show for Admin users (not for customers) */}
      {!isCustomer && (
        <SlideBar
          activeView={activeView}
          onViewChange={handleViewChange} // <-- Use the correct handler
          user={user}
          open={sidebarOpen}
          setOpen={setSidebarOpen}
          onLogout={onLogout}
          onProfileClick={handleProfileClick}
        />
      )}
      {/* Main Content Area */}
      <div
        className={`main-content${!sidebarOpen && !isCustomer ? ' sidebar-collapsed' : ''}${isCustomer ? ' customer-view' : ''}${filtersActive ? ' filters-active' : ''}${filtersActive && viewActive && !headerExpanded ? ' header-compact' : ''}`}
      >
        {/* Project Filters - Show for customers but simplified */}
        {!(activeView === 'admin' || activeView === 'logs' || showProfilePage) && (
          <div className="filters-section">
            <ProjectFilter
              onFilterChange={filters => setProjectFilters(filters)}
              selectedFilters={projectFilters}
              activeView={activeView}
              onViewChange={handleViewChange}
              isCustomer={isCustomer}
              getTabViews={getTabViews}
            />
          </div>
        )}
        {/* Content header for all views */}
        <header 
          className={`content-header${filtersActive && viewActive && !headerExpanded ? ' compact' : ''}${filtersActive && viewActive ? ' clickable' : ''}`}
          onClick={handleHeaderClick}
        >
          <div className="header-content">
            <div className="header-left">
              <h1>
                {showProfilePage 
                  ? 'User Profile'
                  : isCustomer 
                    ? 'Customer Dashboard' 
                    : getTabViews().find(v => v.id === activeView)?.label || 'Dashboard'
                }
              </h1>
              {filtersActive && viewActive && !headerExpanded && (
                <div className="header-subtitle">
                  <span className="expand-hint">Click to expand</span>
                </div>
              )}
            </div>
            <div className="header-actions">
              {/* Only show logout button for customers since they don't have sidebar */}
              {isCustomer && (
                <button 
                  className="btn btn-outline"
                  onClick={onLogout}
                >
                  Logout
                </button>
              )}
            </div>
          </div>
        </header>
        <main className="content-body">{renderDashboardContent()}</main>
      </div>
    </div>
  );
}

export default Dashboard;
