import React, { useState } from 'react';
import './Dashboard.css';
import AdminPanel from './AdminPanel';
import DataVisualization from './DataVisualization';
import LogsNavigation from './LogsNavigation';
import ManagerView from './ManagerView';
import LeadView from './LeadView';
import CustomerView from './CustomerView';
import ProjectFilter from './ProjectFilter';
import SlideBar from './SlideBar';

function Dashboard({ user, onLogout }) {
  const [activeView, setActiveView] = useState(null); // No view by default
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [projectFilters, setProjectFilters] = useState({ domain_id: '', project_id: '' });
  const [clickedButton, setClickedButton] = useState(null);

  // Check if user is a customer
  const isCustomer = user?.role_name === 'Customer';

  // Get user initials for avatar
  const getUserInitials = (name) => {
    if (!name) return 'U';
    return name.split(' ').map(word => word.charAt(0)).join('').toUpperCase().slice(0, 2);
  };

  // Only show Engineer, Lead, Manager as view tabs (not for customers)
  const getTabViews = () => {
    // If user is a customer, don't show any tab views
    if (isCustomer) {
      return [];
    }

    const views = [];
    if (projectFilters.domain_id) {
      views.push(
      { id: 'engineer', label: 'Engineer View', icon: 'âš™ï¸' },
      { id: 'lead', label: 'Lead View', icon: 'ðŸ‘¥' },
        { id: 'manager', label: 'Manager View', icon: 'ðŸ“ˆ' }
      );
    }
    return views;
  };

  // Handle view change with animation
  const handleViewChange = (viewId) => {
    setClickedButton(viewId);
    setActiveView(viewId);

    // Reset the clicked state after animation
    setTimeout(() => {
      setClickedButton(null);
    }, 300);
  };

  // Admin Panel and Logs only from sidebar (not for customers)
  const renderDashboardContent = () => {
    // If user is a customer, always show CustomerView
    if (isCustomer) {
      return <CustomerView user={user} projectFilters={projectFilters} />;
    }

    if (activeView === 'admin') return <AdminPanel />;
    if (activeView === 'logs') return <LogsNavigation />;
    if (!activeView) {
      return (
        <div className="no-view-selected">
          <div className="no-view-content">
            <h2>Please select a view above</h2>
            <p>Choose Engineer, Lead, or Manager to see dashboard data.</p>
          </div>
        </div>
      );
    }
    switch (activeView) {
      case 'engineer':
        return <DataVisualization projectFilters={projectFilters} />;
      case 'lead':
        return <LeadView user={user} projectFilters={projectFilters} />;
      case 'manager':
        return <ManagerView user={user} projectFilters={projectFilters} />;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar - Only show for Admin users (not for customers) */}
      {!isCustomer && (
        <SlideBar
          activeView={activeView}
          onViewChange={setActiveView}
          user={user}
          open={sidebarOpen}
          setOpen={setSidebarOpen}
          onLogout={onLogout}
        />
      )}
      {/* Main Content Area */}
      <div
        className={`main-content${!sidebarOpen && !isCustomer ? ' sidebar-collapsed' : ''}${isCustomer ? ' customer-view' : ''}${(projectFilters.project_id || projectFilters.domain_id) ? ' filters-active' : ''}`}
      >
        {/* Project Filters - Show for customers but simplified */}
        {!(activeView === 'admin' || activeView === 'logs') && (
          <div className="filters-section">
            <ProjectFilter
              onFilterChange={filters => setProjectFilters(filters)}
              selectedFilters={projectFilters}
              activeView={activeView}
              onViewChange={handleViewChange}
              isCustomer={isCustomer}
              getTabViews={getTabViews}
            />
          </div>
        )}
        {/* Content header for all views */}
        <header className="content-header">
          <div className="header-content">
            <div className="header-left">
              <h1>
                {isCustomer 
                  ? 'Customer Dashboard' 
                  : getTabViews().find(v => v.id === activeView)?.label || 'Dashboard'
                }
              </h1>
            </div>
            <div className="header-actions">
              {/* User Profile Section */}
              {user && (
                <div className="user-info">
                  <div className="user-avatar">
                    {getUserInitials(user.name)}
                  </div>
                  <div className="user-details">
                    <div className="user-name">{user.name}</div>
                    <div className="user-role">{user.role_name}</div>
                  </div>
                </div>
              )}
              {/* Add logout button for customers since they don't have sidebar */}
              {isCustomer && (
                <button 
                  className="btn btn-outline"
                  onClick={onLogout}
                  style={{ marginLeft: '1rem' }}
                >
                  Logout
                </button>
              )}
            </div>
          </div>
        </header>
        <main className="content-body">{renderDashboardContent()}</main>
      </div>
    </div>
  );
}

export default Dashboard;
