import { CSSProperties, memo, useMemo } from 'react'
import { useSpring, animated } from '@react-spring/web'
import { useMotionConfig } from '@nivo/core'
import { useTheme } from '@nivo/theming'

interface CrosshairLineProps {
    x0: number
    x1: number
    y0: number
    y1: number
}

export const CrosshairLine = memo(({ x0, x1, y0, y1 }: CrosshairLineProps) => {
    const theme = useTheme()
    const { animate, config: springConfig } = useMotionConfig()
    const style = useMemo(
        () => ({
            ...theme.crosshair.line,
            pointerEvents: 'none' as CSSProperties['pointerEvents'],
        }),
        [theme.crosshair.line]
    )

    const animatedProps = useSpring({
        x1: x0,
        x2: x1,
        y1: y0,
        y2: y1,
        config: springConfig,
        immediate: !animate,
    })

    return <animated.line {...animatedProps} fill="none" style={style} />
})

CrosshairLine.displayName = 'CrosshairLine'
import { memo } from 'react'
import { CrosshairLine } from './CrosshairLine'
import { CrosshairType } from './types'

interface CrosshairProps {
    width: number
    height: number
    type: CrosshairType
    x: number
    y: number
}

export const Crosshair = memo(({ width, height, type, x, y }: CrosshairProps) => {
    let xLine
    let yLine
    if (type === 'cross') {
        xLine = { x0: x, x1: x, y0: 0, y1: height }
        yLine = { x0: 0, x1: width, y0: y, y1: y }
    } else if (type === 'top-left') {
        xLine = { x0: x, x1: x, y0: 0, y1: y }
        yLine = { x0: 0, x1: x, y0: y, y1: y }
    } else if (type === 'top') {
        xLine = { x0: x, x1: x, y0: 0, y1: y }
    } else if (type === 'top-right') {
        xLine = { x0: x, x1: x, y0: 0, y1: y }
        yLine = { x0: x, x1: width, y0: y, y1: y }
    } else if (type === 'right') {
        yLine = { x0: x, x1: width, y0: y, y1: y }
    } else if (type === 'bottom-right') {
        xLine = { x0: x, x1: x, y0: y, y1: height }
        yLine = { x0: x, x1: width, y0: y, y1: y }
    } else if (type === 'bottom') {
        xLine = { x0: x, x1: x, y0: y, y1: height }
    } else if (type === 'bottom-left') {
        xLine = { x0: x, x1: x, y0: y, y1: height }
        yLine = { x0: 0, x1: x, y0: y, y1: y }
    } else if (type === 'left') {
        yLine = { x0: 0, x1: x, y0: y, y1: y }
    } else if (type === 'x') {
        xLine = { x0: x, x1: x, y0: 0, y1: height }
    } else if (type === 'y') {
        yLine = { x0: 0, x1: width, y0: y, y1: y }
    }

    return (
        <>
            {xLine && <CrosshairLine x0={xLine.x0} x1={xLine.x1} y0={xLine.y0} y1={xLine.y1} />}
            {yLine && <CrosshairLine x0={yLine.x0} x1={yLine.x1} y0={yLine.y0} y1={yLine.y1} />}
        </>
    )
})

Crosshair.displayName = 'Crosshair'
import { createContext, MouseEvent, TouchEvent } from 'react'
import { TooltipAnchor } from './types'

export interface TooltipActionsContextData {
    showTooltipAt: (
        content: JSX.Element,
        position: [number, number],
        anchor?: TooltipAnchor
    ) => void
    showTooltipFromEvent: (
        content: JSX.Element,
        event: MouseEvent | TouchEvent,
        anchor?: TooltipAnchor
    ) => void
    hideTooltip: () => void
}

const defaultActions: TooltipActionsContextData = {
    // eslint-disable-next-line @typescript-eslint/no-empty-function
    showTooltipAt: () => {},
    // eslint-disable-next-line @typescript-eslint/no-empty-function
    showTooltipFromEvent: () => {},
    // eslint-disable-next-line @typescript-eslint/no-empty-function
    hideTooltip: () => {},
}

export const TooltipActionsContext = createContext<TooltipActionsContextData>(defaultActions)

export interface TooltipStateContextDataVisible {
    isVisible: true
    position: [number, number]
    content: JSX.Element
    anchor: TooltipAnchor
}

export interface TooltipStateContextDataHidden {
    isVisible: false
    position: [null, null]
    content: null
    anchor: null
}

export type TooltipStateContextData = TooltipStateContextDataVisible | TooltipStateContextDataHidden

export const hiddenTooltipState: TooltipStateContextDataHidden = {
    isVisible: false,
    position: [null, null],
    content: null,
    anchor: null,
}

export const TooltipStateContext = createContext<TooltipStateContextData>(hiddenTooltipState)
import {
    useState,
    useContext,
    useCallback,
    MutableRefObject,
    MouseEvent,
    TouchEvent,
    useMemo,
} from 'react'
import {
    TooltipActionsContext,
    TooltipActionsContextData,
    TooltipStateContext,
    TooltipStateContextData,
    hiddenTooltipState,
} from './context'
import { TooltipAnchor } from './types'

export const useTooltipHandlers = (container: MutableRefObject<HTMLDivElement>) => {
    const [state, setState] = useState<TooltipStateContextData>(hiddenTooltipState)

    const showTooltipAt: TooltipActionsContextData['showTooltipAt'] = useCallback(
        (content: JSX.Element, [x, y]: [number, number], anchor: TooltipAnchor = 'top') => {
            setState({
                isVisible: true,
                position: [x, y],
                anchor,
                content,
            })
        },
        [setState]
    )

    const showTooltipFromEvent: TooltipActionsContextData['showTooltipFromEvent'] = useCallback(
        (content: JSX.Element, event: MouseEvent | TouchEvent, anchor: TooltipAnchor = 'top') => {
            const bounds = container.current.getBoundingClientRect()
            const offsetWidth = container.current.offsetWidth
            // In a normal situation mouse enter / mouse leave events
            // capture the position ok. But when the chart is inside a scaled
            // element with a CSS transform like: `transform: scale(2);`
            // tooltip are not positioned ok.
            // Comparing original width `offsetWidth` agains scaled
            // width give us the scaling factor to calculate
            // ok mouse position
            const scaling = offsetWidth === bounds.width ? 1 : offsetWidth / bounds.width
            const { clientX, clientY } = 'touches' in event ? event.touches[0] : event
            const x = (clientX - bounds.left) * scaling
            const y = (clientY - bounds.top) * scaling

            if (anchor === 'left' || anchor === 'right') {
                if (x < bounds.width / 2) anchor = 'right'
                else anchor = 'left'
            }

            setState({
                isVisible: true,
                position: [x, y],
                anchor,
                content,
            })
        },
        [container, setState]
    )

    const hideTooltip = useCallback(() => {
        setState(hiddenTooltipState)
    }, [setState])

    const actions: TooltipActionsContextData = useMemo(() => {
        return {
            showTooltipAt,
            showTooltipFromEvent,
            hideTooltip,
        }
    }, [showTooltipAt, showTooltipFromEvent, hideTooltip])

    return {
        actions,
        state,
    }
}

export const useTooltip = () => {
    const context = useContext(TooltipActionsContext)
    if (context === undefined) {
        throw new Error('useTooltip must be used within a TooltipProvider')
    }

    return context
}

export const useTooltipState = () => {
    const context = useContext(TooltipStateContext)
    if (context === undefined) {
        throw new Error('useTooltipState must be used within a TooltipProvider')
    }

    return context
}
