import React, { useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell, ScatterChart, Scatter, LineChart, Line, ZAxis, AreaChart, Area, ComposedChart } from 'recharts';
import './ChatView.css';

const STATUS_COLORS = {
  pass: '#00ff88',
  fail: '#ff4757',
  continue_with_error: '#ffa502',
  unknown: '#8884d8',
};

const METRIC_COLORS = {
  area: '#00d2ff',
  internal_timing: '#ff6b6b',
  utilization: '#4ecdc4',
  interface_timing: '#45b7d1',
  congestion: '#96ceb4',
  runtime: '#ffa726',
  avg_runtime: '#ab47bc'
};

function getRuntimeMinutes(str) {
  if (!str || typeof str !== 'string' || !str.includes(':')) return 0;
  const [h, m] = str.split(':').map(Number);
  return (isNaN(h) ? 0 : h) * 60 + (isNaN(m) ? 0 : m);
}

const ChatView = ({ data, stats }) => {
  const [chartType, setChartType] = useState('stage');

  // --- Enhanced Data Preparation ---
  // 1. Stage-wise stacked bar (existing)
  const stageStatusCounts = (() => {
    const result = {};
    data.forEach(row => {
      const stage = (row.stage || 'Unknown').trim();
      const status = (row.run_status || 'unknown').replace(/\s/g, '').toLowerCase();
      if (!result[stage]) result[stage] = { stage, pass: 0, fail: 0, continue_with_error: 0, unknown: 0 };
      if (result[stage][status] !== undefined) result[stage][status] += 1;
      else result[stage].unknown += 1;
    });
    return Object.values(result);
  })();

  // 2. Stage-wise metrics (NEW)
  const stageMetrics = (() => {
    const result = {};
    data.forEach(row => {
      const stage = (row.stage || 'Unknown').trim();
      if (!result[stage]) {
        result[stage] = {
          stage,
          area: [],
          internal_timing: [],
          utilization: [],
          interface_timing: [],
          congestion: [],
          runtime: [],
          count: 0
        };
      }
      
      // Collect all values for averaging
      if (row['Area(um2)']) result[stage].area.push(parseFloat(row['Area(um2)']));
      if (row['internal_timing']) result[stage].internal_timing.push(parseFloat(row['internal_timing']));
      if (row['utilization']) result[stage].utilization.push(parseFloat(row['utilization']));
      if (row['interface_timing']) result[stage].interface_timing.push(parseFloat(row['interface_timing']));
      if (row['congestion_drc_metrics']) result[stage].congestion.push(parseFloat(row['congestion_drc_metrics']));
      
      // Runtime
      const runtime = getRuntimeMinutes(row['runtime(hr:min)']);
      if (runtime > 0) result[stage].runtime.push(runtime);
      
      result[stage].count += 1;
    });

    // Calculate averages
    return Object.values(result).map(stage => ({
      ...stage,
      avg_area: stage.area.length > 0 ? stage.area.reduce((a, b) => a + b, 0) / stage.area.length : 0,
      avg_internal_timing: stage.internal_timing.length > 0 ? stage.internal_timing.reduce((a, b) => a + b, 0) / stage.internal_timing.length : 0,
      avg_utilization: stage.utilization.length > 0 ? stage.utilization.reduce((a, b) => a + b, 0) / stage.utilization.length : 0,
      avg_interface_timing: stage.interface_timing.length > 0 ? stage.interface_timing.reduce((a, b) => a + b, 0) / stage.interface_timing.length : 0,
      avg_congestion: stage.congestion.length > 0 ? stage.congestion.reduce((a, b) => a + b, 0) / stage.congestion.length : 0,
      avg_runtime: stage.runtime.length > 0 ? stage.runtime.reduce((a, b) => a + b, 0) / stage.runtime.length : 0,
      max_area: stage.area.length > 0 ? Math.max(...stage.area) : 0,
      max_runtime: stage.runtime.length > 0 ? Math.max(...stage.runtime) : 0
    }));
  })();

  // 3. Stage-wise performance comparison (NEW)
  const stagePerformance = stageMetrics.map(stage => ({
    stage: stage.stage,
    avg_runtime: stage.avg_runtime,
    avg_area: stage.avg_area,
    avg_utilization: stage.avg_utilization,
    success_rate: stage.count > 0 ? 
      (data.filter(row => (row.stage || 'Unknown').trim() === stage.stage && 
        (row.run_status || 'unknown').replace(/\s/g, '').toLowerCase() === 'pass').length / stage.count * 100) : 0
  }));

  // 4. Area vs Runtime scatter (enhanced)
  const scatterData = data.map(row => {
    let runtime = 0;
    if (row['runtime(hr:min)']) {
      const [h, m] = row['runtime(hr:min)'].split(':').map(Number);
      runtime = h * 60 + m;
    }
    return {
      area: parseFloat(row['Area(um2)']) || 0,
      runtime,
      status: (row['run status  (pass/fail/continue_with_error)'] || row.run_status || 'unknown').replace(/\s/g, '').toLowerCase(),
      stage: (row.stage || 'Unknown').trim(),
    };
  }).filter(d => d.area > 0 && d.runtime > 0);

  // 5. Status pie (existing)
  const statusCounts = data.reduce((acc, row) => {
    const status = (row['run status  (pass/fail/continue_with_error)'] || row.run_status || 'unknown').replace(/\s/g, '').toLowerCase();
    acc[status] = (acc[status] || 0) + 1;
    return acc;
  }, {});
  const statusPieData = Object.entries(statusCounts).map(([name, value]) => ({ name, value, color: STATUS_COLORS[name] || '#8884d8' }));

  // 6. Time trend (enhanced)
  const timeTrend = (() => {
    const byDate = {};
    data.forEach(row => {
      let date = row['run end time'] || row['run_end_time'] || '';
      if (date.includes('/')) {
        const [d, m, y] = date.split('/');
        date = `${y}-${m.padStart(2, '0')}-${d.padStart(2, '0')}`;
      } else if (date.includes('-')) {
        date = date.split('T')[0];
      }
      if (!byDate[date]) byDate[date] = { date, runs: 0, avg_runtime: 0, total_runtime: 0 };
      byDate[date].runs += 1;
      
      let runtime = 0;
      if (row['runtime(hr:min)']) {
        const [h, m] = row['runtime(hr:min)'].split(':').map(Number);
        runtime = h * 60 + m;
      }
      byDate[date].total_runtime += runtime;
    });
    Object.values(byDate).forEach(d => {
      d.avg_runtime = d.runs ? d.total_runtime / d.runs : 0;
    });
    return Object.values(byDate).sort((a, b) => a.date.localeCompare(b.date));
  })();

  // 7. Heatmap (enhanced)
  const heatmapMatrix = (() => {
    const stageSet = new Set();
    const blockSet = new Set();
    const cellMap = {};
    data.forEach(row => {
      const stage = (row.stage || 'Unknown').trim();
      const block = (row.block_name || row.block || 'Unknown').trim();
      stageSet.add(stage);
      blockSet.add(block);
      const key = `${stage}|||${block}`;
      if (!cellMap[key]) cellMap[key] = { sum: 0, count: 0 };
      cellMap[key].sum += getRuntimeMinutes(row['runtime(hr:min)']);
      cellMap[key].count += 1;
    });
    const stages = Array.from(stageSet);
    const blocks = Array.from(blockSet);
    const matrix = stages.map(stage =>
      blocks.map(block => {
        const key = `${stage}|||${block}`;
        const cell = cellMap[key];
        return cell && cell.count > 0 ? cell.sum / cell.count : null;
      })
    );
    let min = Infinity, max = -Infinity;
    matrix.forEach(row => row.forEach(val => {
      if (val !== null) {
        if (val < min) min = val;
        if (val > max) max = val;
      }
    }));
    return { stages, blocks, matrix, min, max };
  })();

  function getHeatColor(val, min, max) {
    if (val === null) return '#eee';
    const t = (val - min) / (max - min + 0.0001);
    return `rgb(${Math.round(224 - t * 209)},${Math.round(247 - t * 232)},${Math.round(250 - t * 155)})`;
  }

  // --- Enhanced Chart Renderers ---
  const renderStageBar = () => (
    <ResponsiveContainer width="100%" height={340}>
      <BarChart data={stageStatusCounts} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="stage" stroke="#fff" />
        <YAxis allowDecimals={false} stroke="#fff" />
        <Tooltip />
        <Legend />
        <Bar dataKey="pass" stackId="a" fill={STATUS_COLORS.pass} name="Pass" />
        <Bar dataKey="fail" stackId="a" fill={STATUS_COLORS.fail} name="Fail" />
        <Bar dataKey="continue_with_error" stackId="a" fill={STATUS_COLORS.continue_with_error} name="Continue with Error" />
        <Bar dataKey="unknown" stackId="a" fill={STATUS_COLORS.unknown} name="Unknown" />
      </BarChart>
    </ResponsiveContainer>
  );

  // NEW: Stage-wise metrics chart
  const renderStageMetrics = () => (
    <ResponsiveContainer width="100%" height={340}>
      <ComposedChart data={stageMetrics} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="stage" stroke="#fff" />
        <YAxis yAxisId="left" stroke="#fff" />
        <YAxis yAxisId="right" orientation="right" stroke="#fff" />
        <Tooltip />
        <Legend />
        <Bar yAxisId="left" dataKey="avg_area" fill={METRIC_COLORS.area} name="Avg Area (umÂ²)" />
        <Bar yAxisId="left" dataKey="avg_utilization" fill={METRIC_COLORS.utilization} name="Avg Utilization (%)" />
        <Line yAxisId="right" type="monotone" dataKey="avg_runtime" stroke={METRIC_COLORS.runtime} name="Avg Runtime (min)" strokeWidth={3} />
      </ComposedChart>
    </ResponsiveContainer>
  );

  // NEW: Stage performance comparison
  const renderStagePerformance = () => (
    <ResponsiveContainer width="100%" height={340}>
      <BarChart data={stagePerformance} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="stage" stroke="#fff" />
        <YAxis yAxisId="left" stroke="#fff" />
        <YAxis yAxisId="right" orientation="right" stroke="#fff" />
        <Tooltip />
        <Legend />
        <Bar yAxisId="left" dataKey="success_rate" fill="#4ecdc4" name="Success Rate (%)" />
        <Line yAxisId="right" type="monotone" dataKey="avg_runtime" stroke={METRIC_COLORS.runtime} name="Avg Runtime (min)" strokeWidth={3} />
      </BarChart>
    </ResponsiveContainer>
  );

  // NEW: Stage-wise timing analysis
  const renderStageTiming = () => (
    <ResponsiveContainer width="100%" height={340}>
      <AreaChart data={stageMetrics} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="stage" stroke="#fff" />
        <YAxis stroke="#fff" />
        <Tooltip />
        <Legend />
        <Area type="monotone" dataKey="avg_internal_timing" stackId="1" stroke={METRIC_COLORS.internal_timing} fill={METRIC_COLORS.internal_timing} name="Internal Timing" />
        <Area type="monotone" dataKey="avg_interface_timing" stackId="1" stroke={METRIC_COLORS.interface_timing} fill={METRIC_COLORS.interface_timing} name="Interface Timing" />
        <Area type="monotone" dataKey="avg_congestion" stackId="1" stroke={METRIC_COLORS.congestion} fill={METRIC_COLORS.congestion} name="Congestion" />
      </AreaChart>
    </ResponsiveContainer>
  );

  const renderScatter = () => (
    <ResponsiveContainer width="100%" height={340}>
      <ScatterChart>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="area" name="Area (umÂ²)" stroke="#fff" />
        <YAxis dataKey="runtime" name="Runtime (min)" stroke="#fff" />
        <ZAxis range={[100, 400]} />
        <Tooltip cursor={{ strokeDasharray: '3 3' }} />
        <Legend />
        {Object.keys(STATUS_COLORS).map(status => (
          <Scatter
            key={status}
            name={status.charAt(0).toUpperCase() + status.slice(1)}
            data={scatterData.filter(d => d.status === status)}
            fill={STATUS_COLORS[status]}
          />
        ))}
      </ScatterChart>
    </ResponsiveContainer>
  );

  const renderPie = () => (
    <ResponsiveContainer width="100%" height={340}>
      <PieChart>
        <Pie
          data={statusPieData}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={110}
          label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
        >
          {statusPieData.map((entry, idx) => (
            <Cell key={`cell-${idx}`} fill={entry.color} stroke="#fff" strokeWidth={2} />
          ))}
        </Pie>
        <Tooltip />
        <Legend />
      </PieChart>
    </ResponsiveContainer>
  );

  const renderTimeTrend = () => (
    <ResponsiveContainer width="100%" height={340}>
      <LineChart data={timeTrend} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="date" stroke="#fff" />
        <YAxis yAxisId="left" allowDecimals={false} stroke="#fff" />
        <YAxis yAxisId="right" orientation="right" stroke="#fff" />
        <Tooltip />
        <Legend />
        <Line yAxisId="left" type="monotone" dataKey="runs" stroke="#00d2ff" name="Runs" strokeWidth={3} />
        <Line yAxisId="right" type="monotone" dataKey="avg_runtime" stroke="#ff6b6b" name="Avg Runtime (min)" strokeWidth={3} />
      </LineChart>
    </ResponsiveContainer>
  );

  const renderHeatmap = () => (
    <div className="heatmap-container">
      <div className="heatmap-title">Stage vs Block Heatmap (Avg Runtime in min)</div>
      <div className="heatmap-grid" style={{ gridTemplateColumns: `120px repeat(${heatmapMatrix.blocks.length}, 1fr)` }}>
        <div className="heatmap-corner"></div>
        {heatmapMatrix.blocks.map(block => (
          <div key={block} className="heatmap-header">{block}</div>
        ))}
        {heatmapMatrix.stages.map((stage, i) => [
          <div key={stage} className="heatmap-header">{stage}</div>,
          ...heatmapMatrix.matrix[i].map((val, j) => (
            <div key={j} className="heatmap-cell" style={{ background: getHeatColor(val, heatmapMatrix.min, heatmapMatrix.max) }}>
              {val !== null ? val.toFixed(1) : ''}
            </div>
          ))
        ])}
      </div>
      <div className="heatmap-legend">
        <span>{heatmapMatrix.min.toFixed(1)} min</span>
        <div className="heatmap-legend-bar"></div>
        <span>{heatmapMatrix.max.toFixed(1)} min</span>
      </div>
    </div>
  );

  // --- Main Render ---
  return (
    <div className="chat-view">
      <div className="chat-header">
        <h3>ðŸ“Š Engineering Data Analytics</h3>
        <div className="chat-controls">
          <button className={chartType === 'stage' ? 'active' : ''} onClick={() => setChartType('stage')}>Stage Status</button>
          <button className={chartType === 'metrics' ? 'active' : ''} onClick={() => setChartType('metrics')}>Stage Metrics</button>
          <button className={chartType === 'performance' ? 'active' : ''} onClick={() => setChartType('performance')}>Performance</button>
          <button className={chartType === 'timing' ? 'active' : ''} onClick={() => setChartType('timing')}>Timing Analysis</button>
          <button className={chartType === 'scatter' ? 'active' : ''} onClick={() => setChartType('scatter')}>Area vs Runtime</button>
          <button className={chartType === 'pie' ? 'active' : ''} onClick={() => setChartType('pie')}>Status Distribution</button>
          <button className={chartType === 'trend' ? 'active' : ''} onClick={() => setChartType('trend')}>Time Trend</button>
          <button className={chartType === 'heatmap' ? 'active' : ''} onClick={() => setChartType('heatmap')}>Stage vs Block</button>
        </div>
      </div>
      <div className="chat-content">
        <div className="chart-section">
          {chartType === 'stage' && renderStageBar()}
          {chartType === 'metrics' && renderStageMetrics()}
          {chartType === 'performance' && renderStagePerformance()}
          {chartType === 'timing' && renderStageTiming()}
          {chartType === 'scatter' && renderScatter()}
          {chartType === 'pie' && renderPie()}
          {chartType === 'trend' && renderTimeTrend()}
          {chartType === 'heatmap' && renderHeatmap()}
        </div>
      </div>
    </div>
  );
};

export default ChatView;
