import React, { useState, useEffect } from 'react';
import './ProjectFilter.css';

const ProjectFilter = ({ onFilterChange, selectedFilters }) => {
  const [domains, setDomains] = useState([]);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchFilterOptions();
  }, []);

  useEffect(() => {
    if (selectedFilters.domain_id) {
      fetchProjects(selectedFilters.domain_id);
    }
  }, [selectedFilters.domain_id]);

  const fetchFilterOptions = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/data/filter-options', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch filter options');
      }

      const data = await response.json();
      setDomains(data.domains || []);
      setProjects(data.projects || []);
    } catch (error) {
      console.error('Error fetching filter options:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchProjects = async (domainId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/data/filter-options?domain_id=${domainId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch projects');
      }

      const data = await response.json();
      setProjects(data.projects || []);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const handleFilterChange = (filterType, value) => {
    const newFilters = { ...selectedFilters };
    
    if (filterType === 'domain_id') {
      newFilters.domain_id = value;
      newFilters.project_id = ''; // Reset project when domain changes
    } else if (filterType === 'project_id') {
      newFilters.project_id = value;
    }

    onFilterChange(newFilters);
  };

  const clearFilters = () => {
    onFilterChange({
      domain_id: '',
      project_id: ''
    });
  };

  if (loading) {
    return (
      <div className="project-filter-loading">
        <div className="loading-spinner"></div>
        <p>Loading filter options...</p>
      </div>
    );
  }

  return (
    <div className="project-filter-container">
      <div className="filter-header">
        <h3>üîç Project Filters</h3>
        <p>Select project and domain to view specific data</p>
      </div>
      
      <div className="filter-controls">
        <div className="filter-group">
          <label htmlFor="domain-select">Domain:</label>
          <select
            id="domain-select"
            value={selectedFilters.domain_id || ''}
            onChange={(e) => handleFilterChange('domain_id', e.target.value)}
            className="filter-select"
          >
            <option value="">All Domains</option>
            {domains.map((domain) => (
              <option key={domain.id} value={domain.id}>
                {domain.full_name}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="project-select">Project:</label>
          <select
            id="project-select"
            value={selectedFilters.project_id || ''}
            onChange={(e) => handleFilterChange('project_id', e.target.value)}
            className="filter-select"
            disabled={!selectedFilters.domain_id}
          >
            <option value="">All Projects</option>
            {projects.map((project) => (
              <option key={project.id} value={project.id}>
                {project.project_name}
              </option>
            ))}
          </select>
        </div>

        <button 
          onClick={clearFilters} 
          className="clear-filters-btn"
          disabled={!selectedFilters.domain_id && !selectedFilters.project_id}
        >
          Clear Filters
        </button>
      </div>

      {error && (
        <div className="filter-error">
          <p>Error: {error}</p>
        </div>
      )}
    </div>
  );
};

export default ProjectFilter;
