import React, { useState, useEffect, useCallback, useMemo } from 'react';
import './EngineeringCharts.css';
import { 
  LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer 
} from 'recharts';

// Custom Tooltip Component
const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="tooltip-label">{`Stage: ${label}`}</p>
        {payload.map((entry, index) => {
          let displayValue = entry.value;
          let displayName = entry.name;

          // Format timing values
          if (entry.dataKey.includes('internal_timing_')) {
            const timingType = entry.dataKey.split('_').pop().toUpperCase();
            displayName = `Internal Timing ${timingType}`;
            displayValue = entry.value.toFixed(2);
          } else if (entry.dataKey === 'internal_timing') {
            // Handle the combined internal timing string
            displayName = 'Internal Timing';
            displayValue = entry.value; // Keep as string format "WNS/TNS/FEP"
          }

          return (
            <p key={index} className="tooltip-value" style={{ color: entry.color }}>
              {`${displayName}: ${displayValue}`}
            </p>
          );
        })}
      </div>
    );
  }
  return null;
};

// Debounce hook to prevent excessive re-renders
const useDebounce = (value, delay) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
};

// Accept selectedDomainId and/or projectFilters as props
const EngineeringCharts = ({ selectedDomainId, projectFilters }) => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filterOptionsLoading, setFilterOptionsLoading] = useState(false);
  // Replace single metric selection with multi-metric selection (max 2)
  const [selectedMetrics, setSelectedMetrics] = useState(['area', 'internal_timing']);
  const [filters, setFilters] = useState({
    project_id: '',
    experiment: '',
    block_name: ''
  });
  const [filterOptions, setFilterOptions] = useState({});
  // Add toggle state for showing/hiding stage-wise statistics
  const [showStageStatistics, setShowStageStatistics] = useState(false);

  const token = localStorage.getItem('token');

  // Debounce filters to prevent excessive API calls
  const debouncedFilters = useDebounce(filters, 300);

  const metrics = [
    { key: 'area', label: 'Area (μm²)', color: '#8884d8' },
    { key: 'internal_timing', label: 'Internal Timing (WNS/TNS/FEP)', color: '#82ca9d' },
    { key: 'interface_timing', label: 'Interface Timing', color: '#ffc658' },
    { key: 'utilization', label: 'Utilization (%)', color: '#ff7300' },
    { key: 'congestion', label: 'Congestion', color: '#ff0000' },
    { key: 'inst_count', label: 'Instance Count', color: '#00ff00' }
  ];

  // Memoize the fetch functions to prevent unnecessary re-renders
  const fetchFilterOptions = useCallback(async () => {
    try {
      setFilterOptionsLoading(true);
      const queryParams = new URLSearchParams();
      if (selectedDomainId) {
        queryParams.append('domain_id', selectedDomainId);
      }
      if (filters.project_id) {
        queryParams.append('project_id', filters.project_id);
      }
      if (filters.block_name) {
        queryParams.append('block_name', filters.block_name);
      }

      const response = await fetch(`http://localhost:5000/api/data/filter-options?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      if (response.ok) {
        const data = await response.json();
        setFilterOptions(data);
      }
    } catch (error) {
      console.error('Error fetching filter options:', error);
    } finally {
      setFilterOptionsLoading(false);
    }
  }, [selectedDomainId, filters.project_id, filters.block_name, token]);

  const fetchChartData = useCallback(async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();

      if (selectedDomainId) queryParams.append('domain_id', selectedDomainId);
      if (debouncedFilters.project_id) queryParams.append('project_id', debouncedFilters.project_id);
      if (debouncedFilters.experiment) queryParams.append('experiment', debouncedFilters.experiment);
      if (debouncedFilters.block_name) queryParams.append('block_name', debouncedFilters.block_name);

      const response = await fetch(`http://localhost:5000/api/data/pd-data?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const result = await response.json();
        processChartData(result.data);
      }
    } catch (error) {
      console.error('Error fetching chart data:', error);
    } finally {
      setLoading(false);
    }
  }, [selectedDomainId, debouncedFilters, token]);

  useEffect(() => {
    if (selectedDomainId) {
      fetchFilterOptions();
    } else {
      // Clear filter options when no domain is selected
      setFilterOptions({});
    }
  }, [fetchFilterOptions, selectedDomainId]);

  useEffect(() => {
    if (selectedDomainId) {
      fetchChartData();
    } else {
      // Clear chart data when no domain is selected
      setChartData([]);
      setLoading(false);
    }
  }, [fetchChartData, selectedDomainId]);

  // Add a normalization function for stage names
  const normalizeStage = s => {
    if (!s) return 'Unknown';
    const lower = s.trim().toLowerCase();
    if (lower === 'synthesis') return 'synthesis';
    if (lower === 'floorplan') return 'Floorplan';
    if (lower === 'placement') return 'Placement';
    return 'Unknown';
  };

  const processChartData = useCallback((data) => {
    // Define the expected stage order
    const stageOrder = ['synthesis', 'Floorplan', 'Placement'];

    // Group data by stage and calculate metrics
    const stageData = {};

    data.forEach(item => {
      const stage = normalizeStage(item.stage);
      if (!stageData[stage]) {
        stageData[stage] = {
          stage,
          count: 0,
          area_sum: 0,
          internal_timing_wns_sum: 0,
          internal_timing_tns_sum: 0,
          internal_timing_fep_sum: 0,
          interface_timing_sum: 0,
          utilization_sum: 0,
          congestion_sum: 0,
          inst_count_sum: 0,
          pass_count: 0,
          fail_count: 0,
          error_count: 0
        };
      }

      stageData[stage].count++;
      stageData[stage].area_sum += parseFloat(item.area_um2) || 0;

      // Process internal timing - extract WNS, TNS, FEP values from complex strings
      // Example: "Setup: r2r: -0.35/-3.8/264 Hold: NA"
      // We extract: WNS = -0.35, TNS = -3.8, FEP = 264
      let internalTimingWNS = 0;
      let internalTimingTNS = 0;
      let internalTimingFEP = 0;

      if (item.internal_timing) {
        // Look for patterns like "r2r: -0.35/-3.8/264" or "i2r: -0.02/-1.4/241"
        const timingMatches = item.internal_timing.match(/(?:r2r|i2r):\s*([-\d.]+)\/([-\d.]+)\/(\d+)/g);
        if (timingMatches) {
          // Take the first match (usually r2r)
          const firstMatch = timingMatches[0];
          const values = firstMatch.match(/(?:r2r|i2r):\s*([-\d.]+)\/([-\d.]+)\/(\d+)/);
          if (values) {
            internalTimingWNS = parseFloat(values[1]) || 0;
            internalTimingTNS = parseFloat(values[2]) || 0;
            internalTimingFEP = parseFloat(values[3]) || 0;
          }
        }
      }

      stageData[stage].internal_timing_wns_sum += internalTimingWNS;
      stageData[stage].internal_timing_tns_sum += internalTimingTNS;
      stageData[stage].internal_timing_fep_sum += internalTimingFEP;

      // Process interface timing - extract numeric values from complex strings
      let interfaceTimingValue = 0;
      if (item.interface_timing) {
        const timingMatch = item.interface_timing.match(/(-?\d+\.?\d*)/);
        if (timingMatch) {
          interfaceTimingValue = parseFloat(timingMatch[1]);
        }
      }
      stageData[stage].interface_timing_sum += interfaceTimingValue;

      stageData[stage].utilization_sum += parseFloat(item.utilization) || 0;
      stageData[stage].inst_count_sum += parseInt(item.inst_count) || 0;

      // Handle congestion (might be text, try to extract numeric value)
      const congestionValue = parseFloat(item.congestion_drc_metrics) || 0;
      stageData[stage].congestion_sum += congestionValue;

      // Count status
      if (item.run_status === 'pass') stageData[stage].pass_count++;
      else if (item.run_status === 'fail') stageData[stage].fail_count++;
      else if (item.run_status === 'continue_with_error') stageData[stage].error_count++;
    });

    // Calculate averages and format for charts, maintaining stage order
    const processedData = stageOrder.map(stage => {
      const stageInfo = stageData[stage] || {};
      const count = stageInfo.count || 0;
      return {
        stage,
        count,
        area: count > 0 ? Number(stageInfo.area_sum) / count : 0,
        internal_timing: count > 0 ? `${Number(stageInfo.internal_timing_wns_sum) / count}/${Number(stageInfo.internal_timing_tns_sum) / count}/${Number(stageInfo.internal_timing_fep_sum) / count}` : '0/0/0',
        internal_timing_wns: count > 0 ? Number(stageInfo.internal_timing_wns_sum) / count : 0,
        internal_timing_tns: count > 0 ? Number(stageInfo.internal_timing_tns_sum) / count : 0,
        internal_timing_fep: count > 0 ? Number(stageInfo.internal_timing_fep_sum) / count : 0,
        interface_timing: count > 0 ? Number(stageInfo.interface_timing_sum) / count : 0,
        utilization: count > 0 ? Number(stageInfo.utilization_sum) / count : 0,
        congestion: count > 0 ? Number(stageInfo.congestion_sum) / count : 0,
        inst_count: count > 0 ? Number(stageInfo.inst_count_sum) / count : 0,
        pass_rate: count > 0 ? (stageInfo.pass_count / count) * 100 : 0,
        fail_rate: count > 0 ? (stageInfo.fail_count / count) * 100 : 0,
        error_rate: count > 0 ? (stageInfo.error_count / count) * 100 : 0
      };
    });

    setChartData(processedData);

    // Debug: Log the processed data for each internal timing metric
    console.log('Chart Data for WNS:', processedData.map(d => ({ stage: d.stage, wns: d.internal_timing_wns, type: typeof d.internal_timing_wns })));
    console.log('Chart Data for TNS:', processedData.map(d => ({ stage: d.stage, tns: d.internal_timing_tns, type: typeof d.internal_timing_tns })));
    console.log('Chart Data for FEP:', processedData.map(d => ({ stage: d.stage, fep: d.internal_timing_fep, type: typeof d.internal_timing_fep })));

    // Debug: Log the processed data
    console.log('Processed Chart Data:', processedData);
    console.log('Available stages in data:', Object.keys(stageData));

    // Debug: Log timing extraction for first few items
    if (data.length > 0) {
      console.log('Sample timing extraction:');
      data.slice(0, 3).forEach((item, index) => {
        if (item.internal_timing) {
          const timingMatches = item.internal_timing.match(/(?:r2r|i2r):\s*([-\d.]+)\/([-\d.]+)\/(\d+)/g);
          console.log(`Item ${index + 1} - Stage: ${item.stage}, Internal Timing: "${item.internal_timing}"`);
          if (timingMatches) {
            const firstMatch = timingMatches[0];
            const values = firstMatch.match(/(?:r2r|i2r):\s*([-\d.]+)\/([-\d.]+)\/(\d+)/);
            if (values) {
              console.log(`  Extracted - WNS: ${values[1]}, TNS: ${values[2]}, FEP: ${values[3]}`);
            }
          } else {
            console.log(`  No timing pattern found`);
          }
        }
      });
    }
  }, []);

  const handleFilterChange = useCallback((key, value) => {
    setFilters(prev => {
      const newFilters = { ...prev, [key]: value };

      // Reset dependent filters when parent filter changes
      if (key === 'project_id') {
        // Reset experiment and block when project changes
        newFilters.experiment = '';
        newFilters.block_name = '';
        // Show loading state briefly for cascading update
        setFilterOptionsLoading(true);
        setTimeout(() => setFilterOptionsLoading(false), 500);
      } else if (key === 'block_name') {
        // Reset experiment when block changes
        newFilters.experiment = '';
        // Show loading state briefly for cascading update
        setFilterOptionsLoading(true);
        setTimeout(() => setFilterOptionsLoading(false), 500);
      }

      return newFilters;
    });
  }, []);

  // Memoize chart data to prevent unnecessary re-renders
  const memoizedChartData = useMemo(() => chartData, [chartData]);
  const memoizedStatusData = useMemo(() => {
    if (chartData.length === 0) return [];

    return chartData.map(stage => ({
      stage: stage.stage,
      pass: stage.pass_rate,
      fail: stage.fail_rate,
      error: stage.error_rate
    }));
  }, [chartData]);

  const renderChart = () => {
    if (loading) {
      return (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading chart data...</p>
        </div>
      );
    }

    if (chartData.length === 0) {
      return (
        <div className="no-data-container">
          <p>No data available for the selected filters</p>
        </div>
      );
    }

    // const selectedMetricData = metrics.find(m => m.key === selectedMetric); // This line is no longer needed

    return (
      <ResponsiveContainer width="100%" height={400}>
        <BarChart 
          data={memoizedChartData} 
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          // Add these props to prevent blinking and improve performance
          syncId="engineering-charts"
          throttle={100}
          // Add these props for better stability
          layout="horizontal"
          barCategoryGap="20%"
          barGap={0}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis 
            dataKey="stage" 
            // Add these props to prevent x-axis blinking
            allowDataOverflow={false}
            scale="band"
            type="category"
            // Add these props for better axis stability
            axisLine={true}
            tickLine={true}
            tick={{ fontSize: 12, fill: '#b4b7bd' }}
            // Prevent axis from jumping
            minTickGap={10}
            interval={0}
          />
          <YAxis 
            // Add these props for better axis stability
            axisLine={true}
            tickLine={true}
            tick={{ fontSize: 12, fill: '#b4b7bd' }}
          />
          <Tooltip 
            content={<CustomTooltip />}
            // Add animation and styling props
            animationDuration={300}
            animationEasing="ease-in-out"
            // Add these props for better tooltip behavior
            cursor={{ fill: 'rgba(115, 103, 240, 0.1)' }}
            isAnimationActive={true}
          />
          <Legend 
            // Add these props for better legend stability
            verticalAlign="top"
            height={36}
            wrapperStyle={{ paddingBottom: '10px' }}
          />
          {/* Update main chart to render multiple Bars for selected metrics */}
          {selectedMetrics.map(mKey => {
            const metric = metrics.find(m => m.key === mKey);
            return (
              <Bar 
                key={mKey}
                dataKey={mKey}
                fill={metric.color}
                name={metric.label}
                // Add animation props
                animationDuration={300}
                animationEasing="ease-in-out"
                // Add these props for better bar behavior
                radius={[4, 4, 0, 0]}
                // Add hover effects
                onMouseEnter={(data, index) => {
                  // Add custom hover logic if needed
                }}
              />
            );
          })}
        </BarChart>
      </ResponsiveContainer>
    );
  };

  const renderStatusChart = () => {
    if (memoizedStatusData.length === 0) return null;

    return (
      <div className="status-chart-container">
        <h3>Run Status by Engineering Stage</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart 
            data={memoizedStatusData} 
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
            // Add syncId to prevent conflicts
            syncId="status-charts"
            throttle={100}
            // Add these props for better stability
            layout="horizontal"
            barCategoryGap="20%"
            barGap={0}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="stage" 
              allowDataOverflow={false}
              scale="band"
              type="category"
              // Add these props for better axis stability
              axisLine={true}
              tickLine={true}
              tick={{ fontSize: 12, fill: '#b4b7bd' }}
              // Prevent axis from jumping
              minTickGap={10}
              interval={0}
            />
            <YAxis 
              // Add these props for better axis stability
              axisLine={true}
              tickLine={true}
              tick={{ fontSize: 12, fill: '#b4b7bd' }}
            />
            <Tooltip 
              content={<CustomTooltip />}
              animationDuration={300}
              animationEasing="ease-in-out"
              // Add these props for better tooltip behavior
              cursor={{ fill: 'rgba(115, 103, 240, 0.1)' }}
              isAnimationActive={true}
            />
            <Legend 
              // Add these props for better legend stability
              verticalAlign="top"
              height={36}
              wrapperStyle={{ paddingBottom: '10px' }}
            />
            <Bar 
              dataKey="pass" 
              stackId="a" 
              fill="#16a34a" 
              name="Pass Rate (%)"
              animationDuration={300}
              animationEasing="ease-in-out"
              radius={[4, 4, 0, 0]}
            />
            <Bar 
              dataKey="fail" 
              stackId="a" 
              fill="#dc2626" 
              name="Fail Rate (%)"
              animationDuration={300}
              animationEasing="ease-in-out"
              radius={[4, 4, 0, 0]}
            />
            <Bar 
              dataKey="error" 
              stackId="a" 
              fill="#f59e0b" 
              name="Error Rate (%)"
              animationDuration={300}
              animationEasing="ease-in-out"
              radius={[4, 4, 0, 0]}
            />
          </BarChart>
        </ResponsiveContainer>
      </div>
    );
  };

  return (
    <div className="engineering-charts">
      <div className="charts-header">
        <h2>Engineering Charts Dashboard</h2>
        <p>Visualize engineering data by stages and metrics</p>
      </div>

      {/* Show message when no domain is selected */}
      {!selectedDomainId && (
        <div className="no-domain-message">
          <div className="message-container">
            <div className="message-icon">📊</div>
            <h3>Please Select a Domain</h3>
            <p>Choose a domain from the main engineering view to see the charts and filter options.</p>
          </div>
        </div>
      )}

      {/* Filters Section - Only show when domain is selected */}
      {selectedDomainId && (
        <div className="filters-section">
          {/* Removed Project Filter Dropdown */}
          <div className={`filter-group ${filterOptionsLoading ? 'loading' : ''}`}>
            <label>Experiment:</label>
            <select 
              value={filters.experiment} 
              onChange={(e) => handleFilterChange('experiment', e.target.value)}
              disabled={filterOptionsLoading}
            >
              <option value="">All Experiments</option>
              {filterOptionsLoading ? (
                <option value="">Loading Experiments...</option>
              ) : (
                filterOptions.experiments?.map(experiment => (
                  <option key={experiment} value={experiment}>
                    {experiment}
                  </option>
                ))
              )}
            </select>
          </div>

          <div className={`filter-group ${filterOptionsLoading ? 'loading' : ''}`}>
            <label>Block Name:</label>
            <select 
              value={filters.block_name} 
              onChange={(e) => handleFilterChange('block_name', e.target.value)}
              disabled={filterOptionsLoading}
            >
              <option value="">All Blocks</option>
              {filterOptionsLoading ? (
                <option value="">Loading Blocks...</option>
              ) : (
                filterOptions.blocks?.map(block => (
                  <option key={block} value={block}>
                    {block}
                  </option>
                ))
              )}
            </select>
          </div>
        </div>
      )}

      {/* Metric Selection Buttons - Only show when domain is selected */}
      {selectedDomainId && (
        <div className="metric-checkboxes">
          <h3>Select up to 2 Metrics to Display</h3>
          <div className="metric-checkboxes-grid">
            {metrics.map(metric => (
              <label key={metric.key} className="metric-checkbox-label">
                <input
                  type="checkbox"
                  checked={selectedMetrics.includes(metric.key)}
                  onChange={() => {
                    setSelectedMetrics(prev => {
                      if (prev.includes(metric.key)) {
                        // Remove metric
                        return prev.filter(m => m !== metric.key);
                      } else if (prev.length < 2) {
                        // Add metric if less than 2 selected
                        return [...prev, metric.key];
                      } else {
                        // Max 2 metrics
                        return prev;
                      }
                    });
                  }}
                  disabled={
                    !selectedMetrics.includes(metric.key) && selectedMetrics.length >= 2
                  }
                />
                <span className="metric-checkbox-icon" style={{ backgroundColor: metric.color }}></span>
                <span className="metric-checkbox-label-text">{metric.label}</span>
              </label>
            ))}
          </div>
        </div>
      )}

      {/* Main Chart - Only show when domain is selected */}
      {selectedDomainId && (
        <div className="main-chart-container">
          <h3>
            {selectedMetrics.map(mKey => metrics.find(m => m.key === mKey)?.label).join(' & ')} by Engineering Stage
          </h3>
          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>Loading chart data...</p>
            </div>
          ) : chartData.length === 0 ? (
            <div className="no-data-container">
              <p>No data available for the selected filters</p>
            </div>
          ) : (
            <>
              {/* Special case: Internal Timing selected, show 3 separate charts */}
              {selectedMetrics.includes('internal_timing') ? (
                <div className="internal-timing-charts-group">
                  {/* WNS Chart */}
                  <div className="internal-timing-chart">
                    <h4>Internal Timing WNS by Engineering Stage</h4>
                    <ResponsiveContainer width="100%" height={250}>
                      <LineChart
                        data={memoizedChartData}
                        margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                        syncId="engineering-charts-wns"
                      >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="stage" allowDataOverflow={false} scale="band" type="category" axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} minTickGap={10} interval={0} />
                        <YAxis axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} />
                        <Tooltip content={<CustomTooltip />} />
                        <Legend verticalAlign="top" height={36} wrapperStyle={{ paddingBottom: '10px' }} />
                        <Line type="monotone" dataKey="internal_timing_wns" stroke="#82ca9d" name="Internal Timing WNS" strokeWidth={3} dot={true} activeDot={{ r: 7 }} />
                      </LineChart>
                    </ResponsiveContainer>
                  </div>
                  {/* TNS Chart */}
                  <div className="internal-timing-chart">
                    <h4>Internal Timing TNS by Engineering Stage</h4>
                    <ResponsiveContainer width="100%" height={250}>
                      <LineChart
                        data={memoizedChartData}
                        margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                        syncId="engineering-charts-tns"
                      >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="stage" allowDataOverflow={false} scale="band" type="category" axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} minTickGap={10} interval={0} />
                        <YAxis axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} />
                        <Tooltip content={<CustomTooltip />} />
                        <Legend verticalAlign="top" height={36} wrapperStyle={{ paddingBottom: '10px' }} />
                        <Line type="monotone" dataKey="internal_timing_tns" stroke="#ffc658" name="Internal Timing TNS" strokeWidth={3} dot={true} activeDot={{ r: 7 }} />
                      </LineChart>
                    </ResponsiveContainer>
                  </div>
                  {/* FEP Chart */}
                  <div className="internal-timing-chart">
                    <h4>Internal Timing FEP by Engineering Stage</h4>
                    <ResponsiveContainer width="100%" height={250}>
                      <LineChart
                        data={memoizedChartData}
                        margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                        syncId="engineering-charts-fep"
                      >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="stage" allowDataOverflow={false} scale="band" type="category" axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} minTickGap={10} interval={0} />
                        <YAxis axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} />
                        <Tooltip content={<CustomTooltip />} />
                        <Legend verticalAlign="top" height={36} wrapperStyle={{ paddingBottom: '10px' }} />
                        <Line type="monotone" dataKey="internal_timing_fep" stroke="#ff7300" name="Internal Timing FEP" strokeWidth={3} dot={true} activeDot={{ r: 7 }} />
                      </LineChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              ) : (
                <ResponsiveContainer width="100%" height={400}>
                  <LineChart
                    data={memoizedChartData}
                    margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                    syncId="engineering-charts"
                  >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="stage" allowDataOverflow={false} scale="band" type="category" axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} minTickGap={10} interval={0} />
                    <YAxis axisLine={true} tickLine={true} tick={{ fontSize: 12, fill: '#b4b7bd' }} />
                    <Tooltip content={<CustomTooltip />} />
                    <Legend verticalAlign="top" height={36} wrapperStyle={{ paddingBottom: '10px' }} />
                    {selectedMetrics.map(mKey => {
                      const metric = metrics.find(m => m.key === mKey);
                      return (
                        <Line
                          key={mKey}
                          type="monotone"
                          dataKey={mKey}
                          stroke={metric.color}
                          name={metric.label}
                          strokeWidth={3}
                        />
                      );
                    })}
                  </LineChart>
                </ResponsiveContainer>
              )}
            </>
          )}
        </div>
      )}

      {/* Status Chart - Only show when domain is selected */}
      {selectedDomainId && renderStatusChart()}

      {/* Data Summary - Only show when domain is selected */}
      {selectedDomainId && (
        <div className="data-summary">
          <h3>Data Summary</h3>
          <div className="summary-grid">
            <div className="summary-item">
              <span className="summary-label">Total Stages:</span>
              <span className="summary-value">{chartData.length}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Total Runs:</span>
              <span className="summary-value">
                {chartData.reduce((sum, stage) => sum + stage.count, 0)}
              </span>
            </div>
            {/* Show average for each selected metric */}
            {selectedMetrics.map(mKey => {
              const metric = metrics.find(m => m.key === mKey);

              // Special handling for internal timing - show averages for WNS, TNS, FEP
              if (mKey === 'internal_timing') {
                const avgWNS = chartData.length > 0 
                  ? (chartData.reduce((sum, stage) => sum + stage.internal_timing_wns, 0) / chartData.length).toFixed(2)
                  : '0';
                const avgTNS = chartData.length > 0 
                  ? (chartData.reduce((sum, stage) => sum + stage.internal_timing_tns, 0) / chartData.length).toFixed(2)
                  : '0';
                const avgFEP = chartData.length > 0 
                  ? (chartData.reduce((sum, stage) => sum + stage.internal_timing_fep, 0) / chartData.length).toFixed(2)
                  : '0';

                return (
                  <div className="summary-item" key={mKey}>
                    <span className="summary-label">Average Internal Timing:</span>
                    <span className="summary-value">
                      WNS: {avgWNS}, TNS: {avgTNS}, FEP: {avgFEP}
                    </span>
                  </div>
                );
              }

              return (
                <div className="summary-item" key={mKey}>
                  <span className="summary-label">Average {metric?.label}:</span>
                  <span className="summary-value">
                    {chartData.length > 0 
                      ? (chartData.reduce((sum, stage) => sum + stage[mKey], 0) / chartData.length).toFixed(2)
                      : '0'
                    }
                  </span>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default EngineeringCharts;
