import React from 'react';
import { ResponsiveBar } from '@nivo/bar';

const chartColors = [
  '#7367f0', '#28c76f', '#ff9f43', '#ea5455', '#00cfe8', '#ff5b5c', '#9c8cfc', '#f8d3ff'
];

// Helper to prepare data for bar/line charts
const prepareStageMetricData = (data, metric) => {
  // Group by stage
  const grouped = {};
  data.forEach(row => {
    const stage = row.stage || 'Unknown';
    if (!grouped[stage]) grouped[stage] = [];
    grouped[stage].push(row);
  });
  // Aggregate (average) metric per stage
  return Object.entries(grouped).map(([stage, rows]) => ({
    stage,
    value: rows.reduce((sum, r) => sum + (parseFloat(r[metric]) || 0), 0) / rows.length
  }));
};

const metricLabels = {
  area_um2: 'Area (um²)',
  internal_timing: 'Internal Timing',
  utilization: 'Utilization (%)',
  interface_timing: 'Interface Timing',
  congestion_drc_metrics: 'Congestion/DRC',
};

const metrics = [
  'area_um2',
  'internal_timing',
  'utilization',
  'interface_timing',
  'congestion_drc_metrics',
];

const EngineeringCharts = ({ data }) => {
  return (
    <div className="charts-view-grid">
      {metrics.map((metric, idx) => {
        const chartData = prepareStageMetricData(data, metric);
        return (
          <div key={metric} className="engineering-chart-card">
            <h4>{metricLabels[metric]}</h4>
            <div className="engineering-chart-inner">
              <ResponsiveBar
                data={chartData}
                keys={['value']}
                indexBy="stage"
                margin={{ top: 30, right: 30, bottom: 60, left: 60 }}
                padding={0.3}
                colors={chartColors[idx % chartColors.length]}
                axisBottom={{
                  tickSize: 5,
                  tickPadding: 5,
                  tickRotation: -30,
                  legend: 'Stage',
                  legendPosition: 'middle',
                  legendOffset: 40,
                }}
                axisLeft={{
                  tickSize: 5,
                  tickPadding: 5,
                  tickRotation: 0,
                  legend: metricLabels[metric],
                  legendPosition: 'middle',
                  legendOffset: -50,
                }}
                tooltip={({ id, value, indexValue }) => (
                  <div style={{ color: '#222', background: '#fff', padding: 8, borderRadius: 8 }}>
                    <strong>{indexValue}</strong><br />
                    {metricLabels[metric]}: {value && !isNaN(value) ? value.toFixed(2) : 'N/A'}
                  </div>
                )}
                theme={{
                  axis: {
                    ticks: { text: { fill: '#b4b7bd' } },
                    legend: { text: { fill: '#b4b7bd' } },
                  },
                  legends: { text: { fill: '#b4b7bd' } },
                  tooltip: { container: { background: '#fff', color: '#222' } },
                }}
                enableLabel={false}
                animate
                motionStiffness={90}
                motionDamping={15}
              />
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default EngineeringCharts;
