import React, { useState, useEffect } from 'react';
import './EngineeringCharts.css';
import { 
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, 
  ResponsiveContainer 
} from 'recharts';

const EngineeringCharts = () => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedMetric, setSelectedMetric] = useState('area');
  const [filters, setFilters] = useState({
    domain_id: '',
    project_id: '',
    experiment: '',
    block_name: ''
  });
  const [filterOptions, setFilterOptions] = useState({});

  const token = localStorage.getItem('token');

  const metrics = [
    { key: 'area', label: 'Area (μm²)', color: '#8884d8' },
    { key: 'internal_timing', label: 'Internal Timing', color: '#82ca9d' },
    { key: 'interface_timing', label: 'Interface Timing', color: '#ffc658' },
    { key: 'utilization', label: 'Utilization (%)', color: '#ff7300' },
    { key: 'congestion', label: 'Congestion', color: '#ff0000' },
    { key: 'inst_count', label: 'Instance Count', color: '#00ff00' }
  ];



  useEffect(() => {
    fetchFilterOptions();
    fetchChartData();
  }, [filters]);

  const fetchFilterOptions = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/data/filter-options', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      if (response.ok) {
        const data = await response.json();
        setFilterOptions(data);
      }
    } catch (error) {
      console.error('Error fetching filter options:', error);
    }
  };

  const fetchChartData = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();
      
      if (filters.domain_id) queryParams.append('domain_id', filters.domain_id);
      if (filters.project_id) queryParams.append('project_id', filters.project_id);
      if (filters.experiment) queryParams.append('experiment', filters.experiment);
      if (filters.block_name) queryParams.append('block_name', filters.block_name);
      if (filters.start_date) queryParams.append('start_date', filters.start_date);
      if (filters.end_date) queryParams.append('end_date', filters.end_date);

      const response = await fetch(`http://localhost:5000/api/data/pd-data?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const result = await response.json();
        processChartData(result.data);
      }
    } catch (error) {
      console.error('Error fetching chart data:', error);
    } finally {
      setLoading(false);
    }
  };

  const processChartData = (data) => {
    // Group data by stage and calculate metrics
    const stageData = {};
    
    data.forEach(item => {
      const stage = item.stage || 'Unknown';
      if (!stageData[stage]) {
        stageData[stage] = {
          stage,
          count: 0,
          area_sum: 0,
          internal_timing_sum: 0,
          interface_timing_sum: 0,
          utilization_sum: 0,
          congestion_sum: 0,
          inst_count_sum: 0,
          pass_count: 0,
          fail_count: 0,
          error_count: 0
        };
      }
      
      stageData[stage].count++;
      stageData[stage].area_sum += parseFloat(item.area_um2) || 0;
      stageData[stage].internal_timing_sum += parseFloat(item.internal_timing) || 0;
      stageData[stage].interface_timing_sum += parseFloat(item.interface_timing) || 0;
      stageData[stage].utilization_sum += parseFloat(item.utilization) || 0;
      stageData[stage].inst_count_sum += parseInt(item.inst_count) || 0;
      
      // Handle congestion (might be text, try to extract numeric value)
      const congestionValue = parseFloat(item.congestion_drc_metrics) || 0;
      stageData[stage].congestion_sum += congestionValue;
      
      // Count status
      if (item.run_status === 'pass') stageData[stage].pass_count++;
      else if (item.run_status === 'fail') stageData[stage].fail_count++;
      else if (item.run_status === 'continue_with_error') stageData[stage].error_count++;
    });

    // Calculate averages and format for charts
    const processedData = Object.values(stageData).map(stage => ({
      stage: stage.stage,
      count: stage.count,
      area: stage.count > 0 ? stage.area_sum / stage.count : 0,
      internal_timing: stage.count > 0 ? stage.internal_timing_sum / stage.count : 0,
      interface_timing: stage.count > 0 ? stage.interface_timing_sum / stage.count : 0,
      utilization: stage.count > 0 ? stage.utilization_sum / stage.count : 0,
      congestion: stage.count > 0 ? stage.congestion_sum / stage.count : 0,
      inst_count: stage.count > 0 ? stage.inst_count_sum / stage.count : 0,
      pass_rate: stage.count > 0 ? (stage.pass_count / stage.count) * 100 : 0,
      fail_rate: stage.count > 0 ? (stage.fail_count / stage.count) * 100 : 0,
      error_rate: stage.count > 0 ? (stage.error_count / stage.count) * 100 : 0
    }));

    setChartData(processedData);
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const renderChart = () => {
    if (loading) {
      return (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading chart data...</p>
        </div>
      );
    }

    if (chartData.length === 0) {
      return (
        <div className="no-data-container">
          <p>No data available for the selected filters</p>
        </div>
      );
    }

    const selectedMetricData = metrics.find(m => m.key === selectedMetric);

    return (
      <ResponsiveContainer width="100%" height={400}>
        <BarChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="stage" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar 
            dataKey={selectedMetric} 
            fill={selectedMetricData.color}
            name={selectedMetricData.label}
          />
        </BarChart>
      </ResponsiveContainer>
    );
  };

  const renderStatusChart = () => {
    if (chartData.length === 0) return null;

    const statusData = chartData.map(stage => ({
      stage: stage.stage,
      pass: stage.pass_rate,
      fail: stage.fail_rate,
      error: stage.error_rate
    }));

    return (
      <div className="status-chart-container">
        <h3>Run Status by Stage</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={statusData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="stage" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="pass" stackId="a" fill="#16a34a" name="Pass Rate (%)" />
            <Bar dataKey="fail" stackId="a" fill="#dc2626" name="Fail Rate (%)" />
            <Bar dataKey="error" stackId="a" fill="#f59e0b" name="Error Rate (%)" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    );
  };

  return (
    <div className="engineering-charts">
      <div className="charts-header">
        <h2>Engineering Charts Dashboard</h2>
        <p>Visualize engineering data by stages and metrics</p>
      </div>

      {/* Filters Section */}
      <div className="filters-section">
        <div className="filter-group">
          <label>Domain:</label>
          <select 
            value={filters.domain_id} 
            onChange={(e) => handleFilterChange('domain_id', e.target.value)}
          >
            <option value="">All Domains</option>
            {filterOptions.domains?.map(domain => (
              <option key={domain.id} value={domain.id}>
                {domain.full_name}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Project:</label>
          <select 
            value={filters.project_id} 
            onChange={(e) => handleFilterChange('project_id', e.target.value)}
          >
            <option value="">All Projects</option>
            {filterOptions.projects?.map(project => (
              <option key={project.id} value={project.id}>
                {project.project_name}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Experiment:</label>
          <select 
            value={filters.experiment} 
            onChange={(e) => handleFilterChange('experiment', e.target.value)}
          >
            <option value="">All Experiments</option>
            {filterOptions.experiments?.map(experiment => (
              <option key={experiment} value={experiment}>
                {experiment}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Block Name:</label>
          <select 
            value={filters.block_name} 
            onChange={(e) => handleFilterChange('block_name', e.target.value)}
          >
            <option value="">All Blocks</option>
            {filterOptions.blocks?.map(block => (
              <option key={block} value={block}>
                {block}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Start Date:</label>
          <input 
            type="date" 
            value={filters.start_date}
            onChange={(e) => handleFilterChange('start_date', e.target.value)}
          />
        </div>

        <div className="filter-group">
          <label>End Date:</label>
          <input 
            type="date" 
            value={filters.end_date}
            onChange={(e) => handleFilterChange('end_date', e.target.value)}
          />
        </div>
      </div>

      {/* Metric Selection Buttons */}
      <div className="metric-buttons">
        <h3>Select Metric to Display</h3>
        <div className="metric-buttons-grid">
          {metrics.map(metric => (
            <button
              key={metric.key}
              className={`metric-button ${selectedMetric === metric.key ? 'active' : ''}`}
              onClick={() => setSelectedMetric(metric.key)}
              style={{ '--metric-color': metric.color }}
            >
              <div className="metric-button-icon" style={{ backgroundColor: metric.color }}></div>
              <span className="metric-button-label">{metric.label}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Main Chart */}
      <div className="main-chart-container">
        <h3>{metrics.find(m => m.key === selectedMetric)?.label} by Stage</h3>
        {renderChart()}
      </div>

      {/* Status Chart */}
      {renderStatusChart()}

      {/* Data Summary */}
      <div className="data-summary">
        <h3>Data Summary</h3>
        <div className="summary-grid">
          <div className="summary-item">
            <span className="summary-label">Total Stages:</span>
            <span className="summary-value">{chartData.length}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Total Runs:</span>
            <span className="summary-value">
              {chartData.reduce((sum, stage) => sum + stage.count, 0)}
            </span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Average {metrics.find(m => m.key === selectedMetric)?.label}:</span>
            <span className="summary-value">
              {chartData.length > 0 
                ? (chartData.reduce((sum, stage) => sum + stage[selectedMetric], 0) / chartData.length).toFixed(2)
                : '0'
              }
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EngineeringCharts;
